const startButton=document.getElementById("start-btn"),nextButton=document.getElementById("next-btn"),questionContainerElement=document.getElementById("question-container"),questionElement=document.getElementById("question"),answerButtonsElement=document.getElementById("answer-btns"),scoreElement=document.getElementById("score"),headerElement=document.getElementById("header");let shuffledQuestions,currentQuestionIndex,score=0;function startGame(){startButton.classList.add("hide"),headerElement.classList.add("hide"),score=0,scoreElement.innerText="Score: "+score+"/"+questions.length,scoreElement.classList.remove("hide"),currentQuestionIndex=0,shuffledQuestions=questions.sort(()=>Math.random()-.5),questionContainerElement.classList.remove("hide"),setNextQuestion()}function setNextQuestion(){resetState(),showQuestion(shuffledQuestions[currentQuestionIndex])}function resetState(){for(nextButton.classList.add("hide"),document.body.classList.add("neutral"),document.body.classList.remove("correct"),document.body.classList.remove("wrong");answerButtonsElement.firstChild;)answerButtonsElement.removeChild(answerButtonsElement.firstChild)}function showQuestion(t){questionElement.innerText=t.question,t.answers.forEach(t=>{const e=document.createElement("button");e.innerText=t.text,e.classList.add("btn"),t.correct&&(e.dataset.correct=t.correct),e.addEventListener("click",selectAnswer),answerButtonsElement.appendChild(e)})}function selectAnswer(t){const e=t.target.dataset.correct;e&&(score++,scoreElement.innerText="Score: "+score+"/"+questions.length),document.body.classList.remove("neutral"),setStatusClass(document.body,e),Array.from(answerButtonsElement.children).forEach(t=>{setStatusClass(t,t.dataset.correct)}),shuffledQuestions.length>currentQuestionIndex+1?nextButton.classList.remove("hide"):(startButton.innerText="Restart",startButton.classList.remove("hide"))}function setStatusClass(t,e){e?t.classList.add("correct"):t.classList.add("wrong")}startButton.addEventListener("click",startGame),nextButton.addEventListener("click",()=>{currentQuestionIndex++,setNextQuestion()});const questions=[{question:"Which isn't a programming language?",answers:[{text:"Java",correct:!1},{text:"Type",correct:!0},{text:"Ruby",correct:!1},{text:"Python",correct:!1}]},{question:"A sequence of steps that must be taken to perform a task is called a(n)...",answers:[{text:"Algorithm",correct:!0},{text:"Loop",correct:!1},{text:"Paragraph",correct:!1},{text:"Protocol",correct:!1}]},{question:"An object can have...",answers:[{text:"Definition",correct:!1},{text:"Logic",correct:!1},{text:"Code",correct:!1},{text:"Properties",correct:!0}]},{question:"The only language a computer can understand is...",answers:[{text:"Tech Language",correct:!1},{text:"Coding Language",correct:!1},{text:"English",correct:!1},{text:"Machine Language",correct:!0}]},{question:"Machine Language instrutions are written in...",answers:[{text:"Logic",correct:!1},{text:"C++",correct:!1},{text:"Binary",correct:!0},{text:"Trinary",correct:!1}]},{question:"Which of the following is a comparison operator?",answers:[{text:">",correct:!0},{text:"@",correct:!1},{text:"*",correct:!1},{text:"^",correct:!1}]},{question:"8 % 3 = __?",answers:[{text:"5",correct:!1},{text:"3",correct:!1},{text:"2",correct:!0},{text:"8",correct:!1}]},{question:"Resolving errors in a program is known as...",answers:[{text:"Discussing",correct:!1},{text:"Brainstorming",correct:!1},{text:"Problem Solving",correct:!1},{text:"Debugging",correct:!0}]},{question:"Which of the following isn't a high-level programming language?",answers:[{text:"Javascript",correct:!1},{text:"Java",correct:!1},{text:"Ruby",correct:!1},{text:"Machine Language",correct:!0}]},{question:"OOP stands for...",answers:[{text:"Object Oriented Programming",correct:!0},{text:"Only Objects Programming",correct:!1},{text:"Overall Oriented Programming",correct:!1},{text:"Object Oriented Path",correct:!1}]}];